.world {
  border-radius: 50%;
  height: 100px;
  width: 100px;
  background-color: aqua;
  overflow: hidden;
}


.world::after,
.world::before {
  background-color: green;
  width: 50px;
  height: 60px;
  content: '';
  display: inline-block;
  float: right;
  border-radius: 30%
}

.world::before {
  margin-top: 84px;
}

